function toggleMarkers(){
	if(document.getElementById('checkbox_showLoc').checked){
		if(document.getElementById('radio_pd').checked) {
			displayMarkers(gMarkers, ps); // Police
		} else if(document.getElementById('radio_hosp').checked) {
			displayMarkers(gMarkers2, hosp); // Hospital 
		} else {
			displayMarkers(gMarkers3, fd); // Fire Department
		}		
	} else {
		clearAllMarkers();
	}
}

function displayMarkers(gMarkerArr, epl) {
	var markerColor;
	if(!gMarkerArr.length){	
		//For loop to create a marker with an infowindow for each emergency personnel location (epl)
		for (var i = 0; i < epl.length; i++) {

			var name = epl[i][1], address = epl[i][2], city = epl[i][3], state = epl[i][4],
				zip = epl[i][5], county = epl[i][6], lat = epl[i][7], lng = epl[i][8],
				itype = epl[i][9];
			latLngSet = new google.maps.LatLng(lat, lng);

			var content = '<div class="map-content">' +
						'<b>' + name + '</b>' + '<br />' +
						'Address: ' + address + '<br />' +
						'City: ' + city + '<br />' +
						'State: ' + state + '<br />' +
						'Zip code: ' + zip + '<br />' +
						'County: ' + county + '</div>';

			if(epl === ps) markerColor = 'blue';
			else if(epl === hosp) markerColor = 'orange';
			else markerColor = 'yellow';

			gMarkerArr[i] = new google.maps.Marker({
				map: gMap,
				title: name,
				position: latLngSet,
				icon: 'https://maps.google.com/mapfiles/ms/icons/' + markerColor + '-dot.png'
			});

			google.maps.event.addListener(gMarkerArr[i], 'click', (function(marker, content) {
	            return function() {
					emergencyInfoWindow.setContent(content);
	                emergencyInfoWindow.open(gMap, marker);
	            }
	        })(gMarkerArr[i], content));
		}
	} else {
		for(var i = 0; i < gMarkerArr.length; i++){
			gMarkerArr[i].setMap(gMap);
		}
	}
}

function populateAndDisplayMarkers() {
	populateLocationsArray();
	displayMarkers(gMarkers, ps); // police
	displayMarkers(gMarkers2, hosp); // hospital
	displayMarkers(gMarkers3, fd); // fire department
}

//Clear all markers on map and uncheck the checkbox
function clearAllMarkers(){
	for(var i = 0; i < gMarkers.length; i++){
		gMarkers[i].setMap(null);
	}
	for(var j = 0; j < gMarkers2.length; j++){
		gMarkers2[j].setMap(null);
	}
	for(var k = 0; k < gMarkers3.length; k++){
		gMarkers3[k].setMap(null);
	}
}
